import Auth from "../auth/auth.js";
class Notes {
  constructor(config) {
    this.config = config instanceof Auth ? config.config : config;
    this.auth = config instanceof Auth ? config : new Auth(config);
  }
  /**
    Get the enablement status of the notes feature set for this tenant and user.
  
    */
  async getSettings() {
    const response = await this.auth.rest(
      "/notes/settings",
      {
        method: "GET"
      }
    );
    return response.json();
  }
  /**
    update the settings
  
      @param data - A JSON payload containing note settings to put.
    */
  async setSettings(data) {
    const response = await this.auth.rest(
      "/notes/settings",
      {
        method: "PUT",
        body: JSON.stringify(data)
      }
    );
    return response.json();
  }
}
export {
  Notes
};
